impl Solution {
    pub fn find_maximum_number(k: i64, x: i32) -> i64 {
        let (mut low, mut high) = (1, k);
        while low < high {
            let mid = high - (high - low) / 2;
            if Solution::price(mid, x) <= k {
                low = mid;
            } else {
                high = mid - 1;
            }
        }
        low
    }

    fn price(n: i64, x: i32) -> i64 {
        let mut sum = 0;
        for i in 1..=x {
            sum += (n >> i) + if i <= (n % x) { 1 } else { 0 };
        }
        sum
    }
}
