object Solution {
    def findMaximumNumber(k: Long, x: Int): Long = {
        var left: Long = 1
        var right: Long = k
        def calcCost(num: Long): Long = {
            var total: Long = 0
            var base: Long = x
            while (base <= num) {
                val full = num / base
                val partial = Math.min(num % base, base / x)
                total += (full + partial) * (base / x)
                if (base > Long.MaxValue / x) return total // Prevent overflow
                base *= x
            }
            total
        }
        while (left < right) {
            val mid = left + (right - left) / 2
            if (calcCost(mid) < k) left = mid + 1
            else right = mid
        }
        if (calcCost(left) <= k) left else left - 1
    }
}
