function findMaximumNumber(k: number, x: number): number {
    let left = 1, right = k;
    const countSetBits = (num: number) => {
        let count = 0;
        while (num) {
            count += num & 1;
            num >>= 1;
        }
        return count;
    };
    const sumOfPrices = (num: number) => {
        let sum = 0;
        for (let i = 1; i <= num; ++i) {
            let setBits = countSetBits(i);
            let price = 0;
            for (let bit = 1; bit <= setBits; ++bit) {
                if ((bit % x) === 0) price++;
            }
            sum += price;
        }
        return sum;
    };
    while (left < right) {
        const mid = Math.ceil((left + right) / 2);
        if (sumOfPrices(mid) <= k) left = mid;
        else right = mid - 1;
    }
    return sumOfPrices(left) <= k ? left : left - 1;
};
