public class Solution {
    public int MaximumSetSize(int[] nums1, int[] nums2) {
        var combinedFreq = new Dictionary<int, int>();

        foreach (var num in nums1) {
            if (!combinedFreq.ContainsKey(num)) combinedFreq[num] = 0;
            combinedFreq[num]++;
        }

        foreach (var num in nums2) {
            if (!combinedFreq.ContainsKey(num)) combinedFreq[num] = 0;
            combinedFreq[num]++;
        }

        var frequencies = combinedFreq.Values.ToList();
        frequencies.Sort((a, b) => b.CompareTo(a));

        int totalRemoved = 0, maxSize = 0;
        foreach (var freq in frequencies) {
            if (totalRemoved < nums1.Length / 2) {
                int toRemove = Math.Min(freq / 2, nums1.Length / 2 - totalRemoved);
                totalRemoved += toRemove;
                maxSize += freq - 2 * toRemove > 0 ? 1 : 0;
            } else {
                maxSize++;
            }
        }

        return maxSize;
    }
}
