#include <stdio.h>
#include <stdlib.h>

int cmp(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int maximumSetSize(int* nums1, int nums1Size, int* nums2, int nums2Size) {
    qsort(nums1, nums1Size, sizeof(int), cmp);
    qsort(nums2, nums2Size, sizeof(int), cmp);

    int totalUnique = 0;
    int i = 0, j = 0;
    while (i < nums1Size && j < nums2Size) {
        if (nums1[i] < nums2[j]) {
            totalUnique++;
            i++;
        } else if (nums1[i] > nums2[j]) {
            totalUnique++;
            j++;
        } else {
            totalUnique++;
            while (i < nums1Size && nums1[i] == nums2[j]) i++;
            while (j < nums2Size && nums2[j] == nums1[i - 1]) j++;
        }
    }

    while (i < nums1Size) {
        totalUnique++;
        i++;
    }

    while (j < nums2Size) {
        totalUnique++;
        j++;
    }

    int toRemove = (nums1Size / 2) + (nums2Size / 2);
    int maxPossible = nums1Size + nums2Size - toRemove;
    return totalUnique > maxPossible ? maxPossible : totalUnique;
}
