func maximumSetSize(nums1 []int, nums2 []int) int {
    allNums := append(nums1, nums2...)
    count := make(map[int]int)
    for _, num := range allNums {
        count[num]++
    }

    counts := make([]int, 0, len(count))
    for _, c := range count {
        counts = append(counts, c)
    }

    sort.Sort(sort.Reverse(sort.IntSlice(counts)))

    n := len(nums1)
    toRemove := n / 2
    removed := 0
    setSize := 0

    for _, c := range counts {
        if toRemove > 0 {
            if c >= 2 && removed < toRemove {
                diff := min(toRemove-removed, c/2)
                removed += diff
                c -= diff * 2
            }
        }
        if c > 0 {
            setSize++
        }
    }

    return setSize
}

