class Solution {
    public int maximumSetSize(int[] nums1, int[] nums2) {
        HashMap<Integer, Integer> frequencyMap = new HashMap<>();
        for (int num : nums1) frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1);
        for (int num : nums2) frequencyMap.put(num, frequencyMap.getOrDefault(num, 0) + 1);
        PriorityQueue<Integer> maxHeap = new PriorityQueue<>((a, b) -> b - a);
        for (int freq : frequencyMap.values()) maxHeap.add(freq);
        int n = nums1.length, removeCount = n / 2, setSize = 0;
        while (!maxHeap.isEmpty() && removeCount > 0) {
            int freq = maxHeap.poll();
            if (freq <= removeCount) {
                removeCount -= freq;
            } else {
                maxHeap.add(freq - removeCount);
                removeCount = 0;
            }
        }
        return maxHeap.size();
    }
}
