class Solution {

    /**
     * @param Integer[] $nums1
     * @param Integer[] $nums2
     * @return Integer
     */
    function maximumSetSize($nums1, $nums2) {
        $count = array_count_values(array_merge($nums1, $nums2));
        $n = count($nums1);
        $freq = [];
        foreach ($count as $num => $c) {
            $freq[] = $c;
        }
        rsort($freq);
        $setSize = 0;
        $removeCount = $n / 2;
        foreach ($freq as $f) {
            if ($removeCount > 0) {
                if ($f > 1) {
                    $remove = min($f - 1, $removeCount);
                    $removeCount -= $remove;
                    $setSize++;
                } else {
                    $setSize++;
                }
            } else {
                $setSize++;
            }
        }
        return min($setSize, $n);
    }
}
