class Solution {
    public long maxStrength(int[] nums) {
        long maxPositiveProduct = 0;
        long maxNegativeProduct = Long.MIN_VALUE;
        long product = 1;
        int negativeCount = 0;
        int zeroCount = 0;
        int maxNegative = Integer.MIN_VALUE;
        for (int num : nums) {
            if (num > 0) {
                maxPositiveProduct = Math.max(maxPositiveProduct, num); // Initialize maxPositiveProduct for single positive number case
                product *= num;
            } else if (num < 0) {
                negativeCount++;
                maxNegative = Math.max(maxNegative, num);
                product *= num;
            } else {
                zeroCount++;
            }
        }
        if (negativeCount % 2 == 0) {
            maxNegativeProduct = product;
        } else if (negativeCount > 1) {
            maxNegativeProduct = product / maxNegative;
        }

        if (negativeCount == 0 && zeroCount == nums.length) {
            return 0; // All zeros
        }

        if (product > 0) { // All positives or even number of negatives
            maxPositiveProduct = product;
        } else if (zeroCount > 0 && (negativeCount == 0 || negativeCount == 1)) {
            maxPositiveProduct = 0; // At least one zero and at most one negative
        }

        return Math.max(maxPositiveProduct, maxNegativeProduct);
    }
}
