/**
 * @param {number[]} nums
 * @return {number}
 */
var maxStrength = function(nums) {
    // Sort the array to handle negative and positive numbers properly
    nums.sort((a, b) => Math.abs(b) - Math.abs(a));
    let maxProduct = nums[0];
    let tempProduct = 1;
    let hasPositive = false;
    let firstNegative = null;
    let lastNegative = null;

    for (let i = 0; i < nums.length; i++) {
        if (nums[i] > 0) {
            hasPositive = true;
            tempProduct *= nums[i];
        } else if (nums[i] < 0) {
            if (firstNegative === null) firstNegative = i;
            lastNegative = i;
            tempProduct *= nums[i];
        }
    }

    if (tempProduct < 0) {
        // If the overall product is negative, try to make it positive by excluding
        // either the first or the last negative number encountered (whichever maximizes the product)
        if (firstNegative !== null && lastNegative !== null && firstNegative !== lastNegative) {
            let productExcludingFirstNegative = tempProduct / nums[firstNegative];
            let productExcludingLastNegative = tempProduct / nums[lastNegative];
            tempProduct = Math.max(productExcludingFirstNegative, productExcludingLastNegative);
        } else if (!hasPositive) {
            // If there are no positive numbers, the max product can only be negative
            // and should be the largest (least negative) single value
            return nums[0];
        }
    }

    return hasPositive || firstNegative !== lastNegative ? tempProduct : maxProduct;
};
