class Solution {
    fun maxStrength(nums: IntArray): Long {
        // Convert nums to a list of Longs for larger product calculation
        val longNums = nums.map { it.toLong() }.sorted()

        // Separate positive and negative numbers
        val positives = longNums.filter { it > 0 }
        val negatives = longNums.filter { it < 0 }

        // Compute the product of positives
        val positiveProduct = positives.fold(1L) { acc, num -> acc * num }

        // Handle negatives: if odd number of negatives, exclude the largest (which is closest to zero)
        val negativeProduct = if (negatives.size % 2 == 1) {
            negatives.dropLast(1).fold(1L) { acc, num -> acc * num }
        } else {
            negatives.fold(1L) { acc, num -> acc * num }
        }

        // Consider zero case: if there are no positives and at least one zero, result is 0
        // because including a zero in the product would maximize the strength to 0
        if (positives.isEmpty() && longNums.contains(0L)) {
            return 0
        }

        // Calculate the final product, considering only positive or only negative or both
        return if (negatives.isEmpty()) {
            positiveProduct
        } else {
            positiveProduct * negativeProduct
        }
    }
}
