class Solution {
    fun maxStrength(nums: IntArray): Long {
        // Special case for single negative number
        if (nums.size == 1) return nums[0].toLong()

        val sortedNums = nums.sortedBy { Math.abs(it) }
        var result = 1L
        var hasPositive = false
        var smallestPositive = Int.MAX_VALUE
        var largestNegative = Int.MIN_VALUE

        for (num in sortedNums) {
            if (num > 0) {
                hasPositive = true
                smallestPositive = Math.min(smallestPositive, num)
            }
            if (num < 0) {
                largestNegative = Math.max(largestNegative, num)
            }
            result *= num
        }

        // If result is positive or there's no positive number and result is negative (even count of negatives)
        if (result > 0 || (!hasPositive && result < 0 && nums.count { it < 0 } % 2 == 0)) {
            return result
        }

        // If all negatives and odd count, remove the negative closest to zero
        if (!hasPositive) {
            return result / largestNegative
        }

        // If mixed and result is negative, adjust based on smallest positive or largest negative
        if (result < 0) {
            if (Math.abs(smallestPositive) < Math.abs(largestNegative)) {
                return result / largestNegative
            } else {
                return result / smallestPositive
            }
        }

        return result
    }
}
