impl Solution {
    pub fn max_sum(nums: Vec<i32>, m: i32, k: i32) -> i64 {
        use std::collections::HashMap;
        let mut max_sum = 0;
        let k = k as usize;
        let mut window_sum = 0;
        let mut freq_map = HashMap::new();
        for i in 0..nums.len() {
            if i < k {
                *freq_map.entry(nums[i]).or_insert(0) += 1;
                window_sum += nums[i] as i64;
                if i == k - 1 && freq_map.len() >= m as usize {
                    max_sum = max_sum.max(window_sum);
                }
            } else {
                if let Some(count) = freq_map.get_mut(&nums[i-k]) {
                    if *count == 1 {
                        freq_map.remove(&nums[i-k]);
                    } else {
                        *count -= 1;
                    }
                }
                window_sum = window_sum - nums[i-k] as i64 + nums[i] as i64;
                *freq_map.entry(nums[i]).or_insert(0) += 1;
                if freq_map.len() >= m as usize {
                    max_sum = max_sum.max(window_sum);
                }
            }
        }
        max_sum
    }
}
