public class Solution {
    public long MaximumTripletValue(int[] nums) {
        long maxVal = 0;
        int n = nums.Length;
        int maxRight = nums[n - 1];

        // Array to store the maximum value to the right for every element
        int[] maxToRight = new int[n];
        maxToRight[n - 1] = nums[n - 1];

        for (int i = n - 2; i >= 0; i--) {
            maxToRight[i] = Math.Max(maxToRight[i + 1], nums[i]);
        }

        for (int j = n - 2; j >= 1; j--) {
            if (nums[j] < maxToRight[j + 1]) {
                for (int i = j - 1; i >= 0; i--) {
                    long val = (long)(nums[i] - nums[j]) * maxToRight[j + 1];
                    if (val > maxVal) {
                        maxVal = val;
                    }
                }
            }
        }

        return maxVal;
    }
}
