class Solution {
public:
    long long maximumTripletValue(vector<int>& nums) {
        long long ans = 0;
        int n = nums.size();
        vector<int> maxSuffix(n + 1, INT_MIN);
        for (int i = n - 1; i >= 0; --i) {
            maxSuffix[i] = max(maxSuffix[i + 1], nums[i]);
        }
        set<int> leftSet;
        leftSet.insert(nums[0]);
        for (int j = 1; j < n - 1; ++j) {
            if (nums[j] < maxSuffix[j + 1]) {
                auto it = leftSet.lower_bound(nums[j]);
                if (it != leftSet.begin()) {
                    --it;
                    long long val = static_cast<long long>(*it - nums[j]) * maxSuffix[j + 1];
                    ans = max(ans, val);
                }
            }
            leftSet.insert(nums[j]);
        }
        return ans;
    }
};
