class Solution {
public:
    long long maximumTripletValue(vector<int>& nums) {
        long long ans = LLONG_MIN;
        int n = nums.size();
        vector<int> leftMin(n);
        leftMin[0] = nums[0];
        for (int i = 1; i < n; ++i) {
            leftMin[i] = min(leftMin[i - 1], nums[i]);
        }

        // We use a max heap to keep track of the max values encountered so far from the right
        priority_queue<int> maxHeap;
        for (int k = n - 1; k >= 2; --k) {
            maxHeap.push(nums[k]);
            if (k < n - 1) {
                // Calculate the value for every pair (i, j) with j < k
                int j = k - 1;
                long long current = (long long)(nums[j] - leftMin[j - 1]) * maxHeap.top();
                ans = max(ans, current);
            }
        }

        return ans > 0 ? ans : 0;
    }
};
