    long long maxVal = 0;
    int max1 = INT_MIN, max2 = INT_MIN, max3 = INT_MIN;

    // Find the three largest values
    for (int i = 0; i < numsSize; ++i) {
        if (nums[i] > max1) {
            max3 = max2;
            max2 = max1;
            max1 = nums[i];
        } else if (nums[i] > max2) {
            max3 = max2;
            max2 = nums[i];
        } else if (nums[i] > max3) {
            max3 = nums[i];
        }
    }

    // Calculate the minimum value among the first two elements for min subtraction
    int min1 = INT_MAX, min2 = INT_MAX;
    for (int i = 0; i < numsSize; ++i) {
        if (nums[i] < min1) {
            min2 = min1;
            min1 = nums[i];
        } else if (nums[i] < min2) {
            min2 = nums[i];
        }
    }

    // Check if using the two smallest values for subtraction and the largest value for multiplication gives a better result
    long long tempVal = (long long)(min1 - min2) * max1;
    if (tempVal > maxVal) {
        maxVal = tempVal;
    }

    // Calculate max value using the three largest values found earlier
    for (int i = 0; i < numsSize; ++i) {
        if (nums[i] != max1 && nums[i] != max2 && nums[i] != max3) {
            tempVal = (long long)(max1 - nums[i]) * max2;
            if (tempVal > maxVal) {
