class Solution {
    fun maximumTripletValue(nums: IntArray): Long {
        var maxVal = Long.MIN_VALUE
        var secondMaxVal = Long.MIN_VALUE
        val minVals = LongArray(nums.size) { Long.MAX_VALUE }

        for (i in nums.indices.reversed()) {
            if (i < nums.size - 1) {
                maxVal = maxOf(maxVal, nums[i + 1].toLong())
            }
            if (i < nums.size - 2) {
                secondMaxVal = maxOf(secondMaxVal, maxVal * nums[i + 2])
                minVals[i] = secondMaxVal
            }
        }

        var maxProduct = 0L
        for (i in nums.indices) {
            for (j in i + 1 until nums.size - 1) {
                maxProduct = maxOf(maxProduct, (nums[i] - nums[j]).toLong() * minVals[j])
            }
        }

        return if (maxProduct > 0) maxProduct else 0
    }
}
