class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function maximumTripletValue($nums) {
        $n = count($nums);
        $maxProduct = 0;
        $leftMax = array_fill(0, $n, 0);
        $rightMax = array_fill(0, $n, 0);

        $max = PHP_INT_MIN;
        for ($i = 0; $i < $n; $i++) {
            $leftMax[$i] = $max;
            $max = max($max, $nums[$i]);
        }

        $max = PHP_INT_MIN;
        for ($i = $n - 1; $i >= 0; $i--) {
            $rightMax[$i] = $max;
            $max = max($max, $nums[$i]);
        }

        for ($i = 1; $i < $n - 1; $i++) {
            if ($leftMax[$i] > $nums[$i] && $rightMax[$i] > $nums[$i]) {
                $maxProduct = max($maxProduct, ($leftMax[$i] - $nums[$i]) * $rightMax[$i]);
            }
        }

        return $maxProduct;
    }
}
