impl Solution {
    pub fn maximum_triplet_value(nums: Vec<i32>) -> i64 {
        let mut max_value = 0i64;
        let mut max_suffix = vec![0; nums.len()];

        max_suffix[nums.len() - 1] = nums[nums.len() - 1];
        for i in (0..nums.len() - 1).rev() {
            max_suffix[i] = max_suffix[i + 1].max(nums[i]);
        }

        let mut max = 0;
        for j in 1..nums.len() - 1 {
            max = max.max(nums[j - 1]);
            let potential_value = (max - nums[j]) as i64 * max_suffix[j + 1] as i64;
            max_value = max_value.max(potential_value);
        }

        max_value
    }
}
