object Solution {
    def maximumTripletValue(nums: Array[Int]): Long = {
        var maxVal = 0L
        val n = nums.length
        val maxRight = new Array[Int](n)
        maxRight(n - 1) = nums(n - 1)
        for (i <- n - 2 to 0 by -1) {
            maxRight(i) = math.max(maxRight(i + 1), nums(i))
        }
        for (j <- 1 until n - 1) {
            var maxLeft = nums(0)
            for (i <- 1 until j) {
                maxLeft = math.max(maxLeft, nums(i))
            }
            if (nums(j) < maxLeft && nums(j) < maxRight(j + 1)) {
                maxVal = math.max(maxVal, (maxLeft - nums(j)).toLong * maxRight(j + 1))
            }
        }
        maxVal
    }
}
