object Solution {
    def maximumTripletValue(nums: Array[Int]): Long = {
        var max = 0L
        val n = nums.length
        val maxSuffix = Array.fill(n)(0)
        maxSuffix(n - 1) = nums(n - 1)
        for (i <- n - 2 to 0 by -1) {
            maxSuffix(i) = math.max(maxSuffix(i + 1), nums(i))
        }
        val set = scala.collection.mutable.TreeSet[Int]()
        set += nums(0)
        for (j <- 1 until n - 1) {
            val k = maxSuffix(j + 1)
            if (k > nums(j)) {
                set.headOption.foreach { i =>
                    if (i < nums(j)) max = max.max((nums(j) - i).toLong * k)
                }
            }
            set += nums(j)
        }
        max
    }
}
