class Solution {
    func maximumTripletValue(_ nums: [Int]) -> Int {
        let n = nums.count
        var maxVal = 0
        var leftMin = Array(repeating: Int.max, count: n)

        // Fill the leftMin array with the minimum value to the left of each index
        for i in 1..<n {
            leftMin[i] = min(leftMin[i-1], nums[i-1])
        }

        // Use a reverse-iteration to maintain the maximum value to the right
        var rightMax = nums[n-1]
        for k in stride(from: n-2, through: 1, by: -1) {
            if nums[k] > leftMin[k] {
                maxVal = max(maxVal, (leftMin[k] - nums[k]) * rightMax)
            }
            rightMax = max(rightMax, nums[k])
        }

        return maxVal
    }
}
