class Solution {
public:
    int minLengthAfterRemovals(vector<int>& nums) {
        // Since the array is sorted, elements can only be removed in pairs if they are different.
        // If there's an odd number of unique elements, one will remain.
        // Thus, we only need to determine if the count of unique elements is odd or even.
        int uniqueCount = 0;
        for (int i = 0; i < nums.size(); ++i) {
            // Count unique elements by comparing each element with the next one.
            if (i == nums.size() - 1 || nums[i] != nums[i + 1]) uniqueCount++;
        }
        // If uniqueCount is odd, one element cannot be removed and will remain.
        // If uniqueCount is even, all elements can be paired and removed.
        return uniqueCount % 2;
    }
};
