class Solution {
public:
    int minLengthAfterRemovals(vector<int>& nums) {
        int n = nums.size();
        // Initialize a counter for pairs.
        int pairs = 0;
        for (int i = 0; i < n - 1; i++) {
            // Since the array is sorted, check adjacent elements for a possible pair.
            if (nums[i] < nums[i + 1]) {
                // If a valid pair is found, increment the pair counter and skip the next element.
                pairs++;
                i++;
            }
        }
        // Calculate remaining elements after removals.
        int remaining = n - 2 * pairs;
        return remaining;
    }
};
