class Solution {
public:
    int minLengthAfterRemovals(vector<int>& nums) {
        // The problem essentially boils down to removing pairs of distinct integers.
        // Since nums is sorted, identical numbers will be adjacent.
        // The minimum length after removals depends on the parity of the count of distinct numbers.
        // If there's an even number of distinct elements, all can be paired and removed.
        // If there's an odd number, one element will remain after all removals.
        int n = nums.size();
        if (n <= 1) return n; // If the array is too small, return its size.

        int distinct = 1; // Start with 1 because the first element is always distinct.
        for (int i = 1; i < n; ++i) {
            if (nums[i] != nums[i - 1]) {
                // Count distinct elements.
                distinct++;
            }
        }

        // If the number of distinct elements is odd, 1 element remains, else all can be removed.
        return distinct % 2;
    }
};
