public class Solution {
    public long MinimumCost(string source, string target, char[] original, char[] changed, int[] cost) {
        int n = source.Length;
        long totalCost = 0;
        var changeCost = new Dictionary<(char, char), int>();
        var directChange = new bool[n];

        for (int i = 0; i < original.Length; i++) {
            var key = (original[i], changed[i]);
            if (changeCost.ContainsKey(key)) {
                changeCost[key] = Math.Min(changeCost[key], cost[i]);
            } else {
                changeCost.Add(key, cost[i]);
            }
        }

        for (int i = 0; i < n; i++) {
            if (source[i] == target[i]) continue;
            var key = (source[i], target[i]);
            if (changeCost.ContainsKey(key)) {
                totalCost += changeCost[key];
                directChange[i] = true;
            }
        }

        for (int i = 0; i < n; i++) {
            if (source[i] != target[i] && !directChange[i]) {
                long minCost = long.MaxValue;
                foreach (var kvp in changeCost) {
                    if (kvp.Key.Item1 == source[i] && changeCost.ContainsKey((kvp.Key.Item2, target[i]))) {
                        minCost = Math.Min(minCost, kvp.Value + changeCost[(kvp.Key.Item2, target[i])]);
                    }
                }
                if (minCost == long.MaxValue) return -1;
                totalCost += minCost;
            }
        }

        return totalCost;
    }
}
