public class Solution {
    public long MinimumCost(string source, string target, char[] original, char[] changed, int[] cost) {
        int n = source.Length;
        var costMap = new Dictionary<string, int>();

        // Initialize cost for direct conversion and indirect conversions
        for (int i = 0; i < original.Length; i++) {
            string key = original[i] + "->" + changed[i];
            if (!costMap.ContainsKey(key)) {
                costMap[key] = cost[i];
            } else {
                costMap[key] = Math.Min(costMap[key], cost[i]);
            }
        }

        long totalCost = 0;

        for (int i = 0; i < n; i++) {
            if (source[i] == target[i]) continue; // No change required

            string directKey = source[i] + "->" + target[i];
            if (costMap.ContainsKey(directKey)) {
                totalCost += costMap[directKey];
            } else {
                // If direct conversion is not available, look for indirect conversions
                bool found = false;
                int minCost = int.MaxValue;
                foreach (var k in costMap.Keys) {
                    if (k.StartsWith(source[i].ToString()) && costMap.ContainsKey(k[0] + "->" + target[i])) {
                        string secondKey = k[2] + "->" + target[i];
                        if (costMap.ContainsKey(secondKey)) {
                            minCost = Math.Min(minCost, costMap[k] + costMap[secondKey]);
                            found = true;
                        }
                    }
                }

                if (!found) return -1; // Conversion is not possible
                totalCost += minCost;
            }
        }

        return totalCost;
    }
}
