#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>
#include <limits>

using namespace std;

class Solution {
public:
    long long minimumCost(string source, string target, vector<char>& original, vector<char>& changed, vector<int>& cost) {
        // Key: character pair (original, changed), Value: minimum cost to change
        unordered_map<int, int> costMap;
        const int n = original.size();
        for (int i = 0; i < n; ++i) {
            int key = original[i] * 256 + changed[i]; // Create a unique key for each character pair
            if (costMap.find(key) == costMap.end() || costMap[key] > cost[i]) {
                costMap[key] = cost[i];
            }
        }

        long long totalCost = 0;
        for (int i = 0; i < source.size(); ++i) {
            if (source[i] == target[i]) continue; // No operation needed if characters are the same

            int key = source[i] * 256 + target[i];
            if (costMap.find(key) != costMap.end()) {
                totalCost += costMap[key];
            } else {
                // If direct conversion is not possible or not the cheapest option
                return -1; // Conversion impossible with the given mappings
            }
        }

        return totalCost;
    }
};
