long long minimumCost(char* source, char* target, char* original, int originalSize, char* changed, int changedSize, int* cost, int costSize) {
    long long dp[26][26];
    for (int i = 0; i < 26; ++i) {
        for (int j = 0; j < 26; ++j) {
            dp[i][j] = (i == j) ? 0 : INT_MAX;
        }
    }
    for (int i = 0; i < originalSize; ++i) {
        int o = original[i] - 'a', c = changed[i] - 'a';
        if (cost[i] < dp[o][c]) {
            dp[o][c] = cost[i];
        }
    }
    for (int k = 0; k < 26; ++k) {
        for (int i = 0; i < 26; ++i) {
            for (int j = 0; j < 26; ++j) {
                if (dp[i][k] != INT_MAX && dp[k][j] != INT_MAX && dp[i][k] + dp[k][j] < dp[i][j]) {
                    dp[i][j] = dp[i][k] + dp[k][j];
                }
            }
        }
    }
    long long totalCost = 0;
    for (int i = 0; source[i] && target[i]; ++i) {
        int s = source[i] - 'a', t = target[i] - 'a';
        if (dp[s][t] == INT_MAX) return -1;
        totalCost += dp[s][t];
    }
    return totalCost;
}
