func minimumCost(source string, target string, original []byte, changed []byte, cost []int) int64 {
    // map to store minimum cost to change from one character to another
    minCostChange := make(map[byte]map[byte]int)
    for i := 0; i < len(original); i++ {
        if _, ok := minCostChange[original[i]]; !ok {
            minCostChange[original[i]] = make(map[byte]int)
        }
        if c, ok := minCostChange[original[i]][changed[i]]; !ok || cost[i] < c {
            minCostChange[original[i]][changed[i]] = cost[i]
        }
    }

    totalCost := int64(0)
    for i := 0; i < len(source); i++ {
        if source[i] == target[i] {
            continue
        }
        if changeCost, ok := minCostChange[source[i]][target[i]]; ok {
            totalCost += int64(changeCost)
        } else {
            return -1
        }
    }

    return totalCost
}
