/**
 * @param {string} source
 * @param {string} target
 * @param {character[]} original
 * @param {character[]} changed
 * @param {number[]} cost
 * @return {number}
 */
var minimumCost = function(source, target, original, changed, cost) {
    let totalCost = 0;
    const changesMap = new Map();

    for (let i = 0; i < original.length; i++) {
        const key = original[i] + ',' + changed[i];
        if (!changesMap.has(key)) changesMap.set(key, []);
        changesMap.get(key).push(cost[i]);
    }

    for (const [key, values] of changesMap.entries()) {
        changesMap.set(key, Math.min(...values));
    }

    for (let i = 0; i < source.length; i++) {
        if (source[i] === target[i]) continue;
        const key = source[i] + ',' + target[i];
        if (!changesMap.has(key)) return -1;
        totalCost += changesMap.get(key);
    }

    return totalCost;
};
