/**
 * @param {string} source
 * @param {string} target
 * @param {character[]} original
 * @param {character[]} changed
 * @param {number[]} cost
 * @return {number}
 */
var minimumCost = function(source, target, original, changed, cost) {
    const dp = new Array(source.length + 1).fill(0).map(() => new Array(26).fill(Infinity));
    const costMap = new Map();

    for (let i = 0; i < original.length; i++) {
        const o = original[i].charCodeAt(0) - 'a'.charCodeAt(0);
        const c = changed[i].charCodeAt(0) - 'a'.charCodeAt(0);
        if (!costMap.has(o)) costMap.set(o, new Array(26).fill(Infinity));
        costMap.get(o)[c] = Math.min(costMap.get(o)[c], cost[i]);
    }

    for (let i = 0; i < 26; i++) dp[0][i] = 0;

    for (let i = 1; i <= source.length; i++) {
        const sChar = source.charCodeAt(i - 1) - 'a'.charCodeAt(0);
        const tChar = target.charCodeAt(i - 1) - 'a'.charCodeAt(0);

        for (let j = 0; j < 26; j++) {
            // Keep the character
            dp[i][tChar] = Math.min(dp[i][tChar], dp[i - 1][j] + (sChar === tChar ? 0 : Infinity));

            // Change the character if possible
            if (costMap.has(sChar) && costMap.get(sChar)[tChar] !== Infinity) {
                dp[i][tChar] = Math.min(dp[i][tChar], dp[i - 1][j] + costMap.get(sChar)[tChar]);
            }
        }
    }

    let minCost = Infinity;
    for (let cost of dp[source.length]) minCost = Math.min(minCost, cost);

    return minCost === Infinity ? -1 : minCost;
};
