class Solution {
    fun minimumCost(source: String, target: String, original: CharArray, changed: CharArray, cost: IntArray): Long {
        val n = source.length
        val costMap = mutableMapOf<Pair<Char, Char>, Int>()

        for (i in original.indices) {
            val key = Pair(original[i], changed[i])
            costMap[key] = costMap.getOrDefault(key, 0) + cost[i]
        }

        var totalCost: Long = 0
        for (i in 0 until n) {
            if (source[i] == target[i]) continue
            val key = Pair(source[i], target[i])
            val changeCost = costMap[key]
            if (changeCost == null) return -1
            totalCost += changeCost
        }

        return totalCost
    }
}
