class Solution {

    /**
     * @param String $source
     * @param String $target
     * @param String[] $original
     * @param String[] $changed
     * @param Integer[] $cost
     * @return Integer
     */
    function minimumCost($source, $target, $original, $changed, $cost) {
        $n = strlen($source);
        $transitions = [];
        for ($i = 0; $i < count($original); $i++) {
            $transitions[$original[$i]][$changed[$i]] = isset($transitions[$original[$i]][$changed[$i]]) ? min($transitions[$original[$i]][$changed[$i]], $cost[$i]) : $cost[$i];
        }

        $totalCost = 0;
        for ($i = 0; $i < $n; $i++) {
            if ($source[$i] == $target[$i]) continue; // No cost if characters are the same

            if (!isset($transitions[$source[$i]][$target[$i]])) return -1; // No direct transition available

            $totalCost += $transitions[$source[$i]][$target[$i]];
        }

        return $totalCost;
    }
}
