class Solution {

    /**
     * @param String $source
     * @param String $target
     * @param String[] $original
     * @param String[] $changed
     * @param Integer[] $cost
     * @return Integer
     */
    function minimumCost($source, $target, $original, $changed, $cost) {
        $n = strlen($source);
        $m = count($cost);
        $dp = array_fill(0, $n+1, array_fill(0, 26, PHP_INT_MAX));
        for ($i = 0; $i < 26; $i++) {
            $dp[$n][$i] = 0; // cost to change from any char to any char at the end is 0
        }

        $charCost = [];
        for ($i = 0; $i < $m; $i++) {
            $o = ord($original[$i]) - ord('a');
            $c = ord($changed[$i]) - ord('a');
            if (!isset($charCost[$o][$c]) || $charCost[$o][$c] > $cost[$i]) {
                $charCost[$o][$c] = $cost[$i];
            }
        }

        for ($i = $n - 1; $i >= 0; $i--) {
            for ($j = 0; $j < 26; $j++) {
                $currentChar = ord($source[$i]) - ord('a');
                $targetChar = ord($target[$i]) - ord('a');
                if ($currentChar == $j) {
                    $dp[$i][$j] = $dp[$i+1][$targetChar]; // no cost if already matches
                } else {
                    foreach ($charCost[$currentChar] ?? [] as $changeTo => $cost) {
                        if ($dp[$i+1][$changeTo] != PHP_INT_MAX) {
                            $dp[$i][$j] = min($dp[$i][$j], $dp[$i+1][$changeTo] + $cost);
                        }
                    }
                }
            }
        }

        $result = $dp[0][ord($source[0]) - ord('a')];
        return $result == PHP_INT_MAX ? -1 : $result;
    }
}
