class Solution {

    /**
     * @param String $source
     * @param String $target
     * @param String[] $original
     * @param String[] $changed
     * @param Integer[] $cost
     * @return Integer
     */
    function minimumCost($source, $target, $original, $changed, $cost) {
        $n = strlen($source);
        $costMap = [];
        for ($i = 0; $i < count($original); $i++) {
            if (!isset($costMap[$original[$i]][$changed[$i]])) {
                $costMap[$original[$i]][$changed[$i]] = $cost[$i];
            } else {
                $costMap[$original[$i]][$changed[$i]] = min($costMap[$original[$i]][$changed[$i]], $cost[$i]);
            }
        }

        $totalCost = 0;
        for ($i = 0; $i < $n; $i++) {
            if ($source[$i] == $target[$i]) {
                continue;
            }

            if (!isset($costMap[$source[$i]][$target[$i]])) {
                return -1; // No direct way to convert this character
            }

            $totalCost += $costMap[$source[$i]][$target[$i]];
        }

        return $totalCost;
    }
}
