class Solution(object):
    def minimumCost(self, source, target, original, changed, cost):
        """
        :type source: str
        :type target: str
        :type original: List[str]
        :type changed: List[str]
        :type cost: List[int]
        :rtype: int
        """
        n = len(source)
        dp = [float('inf')] * (n + 1)
        dp[0] = 0
        change_map = {}
        for i in range(len(original)):
            if original[i] != changed[i]:
                change_map[(original[i], changed[i])] = min(cost[i], change_map.get((original[i], changed[i]), float('inf')))

        for i in range(1, n + 1):
            if source[i-1] == target[i-1]:
                dp[i] = dp[i-1]
            else:
                if (source[i-1], target[i-1]) in change_map:
                    dp[i] = dp[i-1] + change_map[(source[i-1], target[i-1])]

        if dp[n] == float('inf'):
            return -1
        return dp[n]
