object Solution {
    def minimumCost(source: String, target: String, original: Array[Char], changed: Array[Char], cost: Array[Int]): Long = {
        val n = source.length
        val m = original.length
        val costMap = scala.collection.mutable.Map[(Char, Char), Int]().withDefaultValue(Int.MaxValue)

        for (i <- 0 until m) {
            val key = (original(i), changed(i))
            costMap(key) = costMap(key).min(cost(i))
        }

        var totalCost: Long = 0
        for (i <- 0 until n) {
            if (source(i) != target(i)) {
                val changeCost = costMap((source(i), target(i)))
                if (changeCost == Int.MaxValue) return -1
                totalCost += changeCost
            }
        }
        totalCost
    }
}
