object Solution {
    def minimumCost(source: String, target: String, original: Array[Char], changed: Array[Char], cost: Array[Int]): Long = {
        val transitionCost = Array.fill(26, 26)(Int.MaxValue)
        for (i <- original.indices) {
            val o = original(i) - 'a'
            val c = changed(i) - 'a'
            transitionCost(o)(c) = transitionCost(o)(c) min cost(i)
        }

        for (k <- 0 until 26) {
            for (i <- 0 until 26) {
                for (j <- 0 until 26) {
                    if (transitionCost(i)(k) < Int.MaxValue && transitionCost(k)(j) < Int.MaxValue) {
                        transitionCost(i)(j) = transitionCost(i)(j) min (transitionCost(i)(k) + transitionCost(k)(j))
                    }
                }
            }
        }

        var totalCost = 0L
        for (i <- source.indices) {
            val s = source(i) - 'a'
            val t = target(i) - 'a'
            if (s != t) {
                if (transitionCost(s)(t) == Int.MaxValue) return -1
                totalCost += transitionCost(s)(t)
            }
        }
        totalCost
    }
}
