class Solution {
    func minimumCost(_ source: String, _ target: String, _ original: [Character], _ changed: [Character], _ cost: [Int]) -> Int {
        let n = source.count
        var sourceArray = Array(source)
        var targetArray = Array(target)
        var costMap: [Character: [Character: Int]] = [:]

        for i in 0..<original.count {
            costMap[original[i], default: [:]][changed[i], default: 0] = max(costMap[original[i], default: [:]][changed[i], default: 0], cost[i])
        }

        var totalCost = 0
        for i in 0..<n {
            if sourceArray[i] == targetArray[i] {
                continue
            } else if let changes = costMap[sourceArray[i]], let changeCost = changes[targetArray[i]] {
                totalCost += changeCost
            } else {
                return -1
            }
        }
        return totalCost
    }
}
