class Solution {
    func minimumCost(_ source: String, _ target: String, _ original: [Character], _ changed: [Character], _ cost: [Int]) -> Int {
        var adj = [Character: [(Character, Int)]]() // adjacency list: char -> [(char, cost)]
        for i in 0..<original.count {
            let o = original[i], c = changed[i]
            adj[o, default: []].append((c, cost[i]))
        }

        // For each original char, keep only the maximum cost change to a particular char
        var maxCostChange = [Character: [Character: Int]]()
        for (o, changes) in adj {
            var best = [Character: Int]()
            for (c, cost) in changes {
                best[c, default: 0] = max(best[c, default: 0], cost)
            }
            maxCostChange[o] = best
        }

        let s = Array(source), t = Array(target)
        var totalCost = 0
        for i in 0..<s.count {
            if s[i] == t[i] { continue }
            guard let cost = maxCostChange[s[i]]?[t[i]] else { return -1 }
            totalCost += cost
        }
        return totalCost
    }
}
