function minimumCost(source: string, target: string, original: string[], changed: string[], cost: number[]): number {
    let totalCost = 0;
    const map: Map<string, number[]> = new Map();

    // Populate a map where each original character points to an array of costs to change it to other characters.
    for (let i = 0; i < original.length; i++) {
        const key = original[i] + "->" + changed[i];
        if (!map.has(key)) {
            map.set(key, []);
        }
        map.get(key).push(cost[i]);
    }

    // For each transformation, find the minimum cost, if possible.
    for (let i = 0; i < source.length; i++) {
        if (source[i] === target[i]) continue; // No transformation needed.

        const transformKey = source[i] + "->" + target[i];
        if (map.has(transformKey)) {
            const costs = map.get(transformKey);
            if (!costs || costs.length === 0) return -1; // Transformation not possible.

            totalCost += Math.min(...costs); // Choose the minimum cost for the transformation.
        } else {
            return -1; // Transformation not possible.
        }
    }

    return totalCost;
}
