function minimumCost(source: string, target: string, original: string[], changed: string[], cost: number[]): number {
    let totalCost = 0;
    const changesMap: Map<string, number> = new Map();

    // Store the least cost for each possible change.
    for (let i = 0; i < original.length; i++) {
        const key = original[i] + "->" + changed[i];
        if (!changesMap.has(key) || changesMap.get(key) > cost[i]) {
            changesMap.set(key, cost[i]);
        }
    }

    for (let i = 0; i < source.length; i++) {
        if (source[i] === target[i]) continue; // No change needed if characters are the same.

        const changeKey = source[i] + "->" + target[i];
        if (changesMap.has(changeKey)) {
            totalCost += changesMap.get(changeKey);
        } else {
            // If there's no direct way to change source[i] to target[i], check if there's a sequence of changes that can be made.
            let found = false;
            for (let j = 0; j < original.length; j++) {
                if (original[j] === source[i] && changed[j] === target[i]) {
                    totalCost += cost[j];
                    found = true;
                    break;
                }
            }
            if (!found) return -1; // No sequence of changes can achieve the target character.
        }
    }

    return totalCost;
}
