class Solution {
public:
    long long minimumCost(string s) {
        int n = s.size();
        long long cost = 0;
        int zeroCount = 0, oneCount = 0;

        // Count the number of '0's and '1's in the string
        for (char c : s) {
            if (c == '0') zeroCount++;
            else oneCount++;
        }

        // Cost to make all characters '0' or '1' directly
        long long minCost = min(zeroCount, oneCount);

        int flips = 0; // Count of flips needed to make substring uniform
        for (int i = 0; i < n; ++i) {
            // If we encounter '1', it suggests flipping from start to current index
            if (s[i] == '1') flips++;
            else flips--; // If we encounter '0', reduce the flip count as it moves towards uniformity

            // Reset flips to zero if it's negative, as negative flips don't make sense
            if (flips < 0) flips = 0;

            // Update the minimum cost considering the cost of flips so far
            minCost = min(minCost, (long long)flips + min(zeroCount, oneCount) - min(i + 1 - zeroCount, zeroCount));

            // Adjust the zeroCount as we move through the string
            if (s[i] == '0') zeroCount--;
        }

        return minCost;
    }
};
