defmodule Solution do
  @spec minimum_cost(s :: String.t) :: integer
  def minimum_cost(s) do
    s
    |> String.to_charlist()
    |> Enum.reduce({0, 0, 0}, fn
      '0', {zeros, ones, cost} -> {zeros + 1, ones, cost + ones}
      '1', {zeros, ones, cost} -> {zeros, ones + 1, cost + zeros}
    end)
    |> (fn {_, _, cost} -> cost end).()
  end
end
