class Solution {
    public long minimumCost(String s) {
        long cost = 0;
        int n = s.length();
        int[] prefixZeroes = new int[n];
        int[] suffixOnes = new int[n];

        prefixZeroes[0] = s.charAt(0) == '0' ? 1 : 0;
        for (int i = 1; i < n; i++) {
            prefixZeroes[i] = prefixZeroes[i - 1] + (s.charAt(i) == '0' ? 1 : 0);
        }

        suffixOnes[n - 1] = s.charAt(n - 1) == '1' ? 1 : 0;
        for (int i = n - 2; i >= 0; i--) {
            suffixOnes[i] = suffixOnes[i + 1] + (s.charAt(i) == '1' ? 1 : 0);
        }

        long minCost = Long.MAX_VALUE;
        for (int i = 0; i < n; i++) {
            long costToMakeAllZeroes = (i + 1) - prefixZeroes[i] + (i < n - 1 ? suffixOnes[i + 1] : 0);
            long costToMakeAllOnes = prefixZeroes[i] + (i < n - 1 ? (n - i - 1) - suffixOnes[i + 1] : 0);
            minCost = Math.min(minCost, Math.min(costToMakeAllZeroes, costToMakeAllOnes));
        }

        return minCost == Long.MAX_VALUE ? 0 : minCost;
    }
}
