class Solution {

    /**
     * @param String $s
     * @return Integer
     */
    function minimumCost($s) {
        $n = strlen($s);
        $cost = 0;
        $flipCount = 0; // Tracks the number of flips needed to make all characters the same

        for ($i = 0; $i < $n; ++$i) {
            if ($s[$i] == '0') {
                // If a '0' is encountered, consider flipping from the start to this index or from this index to the end
                $cost += min($i + 1, $n - $i);
                $flipCount++; // Increment flip count as a '0' requires action to make all characters '1'
            }
        }

        // If all characters are '1', no cost is needed
        if ($flipCount == 0) return 0;

        // Calculate minimum cost by comparing the cost of flipping to make all '1's or all '0's
        $minCost = PHP_INT_MAX;
        $flipToMakeAllOnes = $flipCount;
        $flipToMakeAllZeros = 0;

        for ($i = 0; $i < $n; ++$i) {
            if ($s[$i] == '0') {
                $flipToMakeAllOnes--; // A '0' that needs to be flipped to make all '1's
            } else {
                $flipToMakeAllZeros++; // A '1' that would need to be flipped to make all '0's
            }
            // Calculate cost at each point and update minimum cost
            $currentCost = $flipToMakeAllOnes * ($i + 1) + $flipToMakeAllZeros * ($n - $i);
            $minCost = min($minCost, $currentCost);
        }

        // Add final pass to consider making all zeros with a flip at the end
        $minCost = min($minCost, $flipToMakeAllZeros * $n);

        return $minCost;
    }
}
