public class Solution {
    public long MinimumCost(int[] nums) {
        long cost = long.MaxValue;
        long total = 0;
        foreach (var num in nums) {
            total += num;
        }
        long avg = total / nums.Length;
        long[] targets = { avg - 1, avg, avg + 1 };
        foreach (var target in targets) {
            if (IsPalindromic(target)) {
                long tempCost = 0;
                foreach (var num in nums) {
                    tempCost += Math.Abs(num - target);
                }
                cost = Math.Min(cost, tempCost);
            }
        }
        // Fallback if no average-based palindromic number minimizes cost
        if (cost == long.MaxValue) {
            cost = 0;
            foreach (var num in nums) {
                cost += Math.Abs(num - nums[0]); // Convert all to first element as last resort
            }
        }
        return cost;
    }

    private bool IsPalindromic(long num) {
        string str = num.ToString();
        for (int i = 0, j = str.Length - 1; i < j; i++, j--) {
            if (str[i] != str[j]) return false;
        }
        return true;
    }
}
