#include <limits.h>
#include <stdlib.h>

int isPalindrome(int x) {
    int reversed = 0, original = x;
    while (x > 0) {
        reversed = reversed * 10 + x % 10;
        x /= 10;
    }
    return original == reversed;
}

int findClosestPalindrome(int x) {
    int lower = x - 1;
    int upper = x + 1;
    while (1) {
        if (isPalindrome(lower)) return lower;
        if (isPalindrome(upper)) return upper;
        lower--;
        upper++;
    }
}

long long minimumCost(int* nums, int numsSize) {
    long long cost = LLONG_MAX;
    for (int i = 0; i < numsSize; i++) {
        int target = findClosestPalindrome(nums[i]);
        long long tempCost = 0;
        for (int j = 0; j < numsSize; j++) {
            tempCost += abs(nums[j] - target);
        }
        if (tempCost < cost) {
            cost = tempCost;
        }
    }
    return cost;
}
